package Ch01;

public class Escape {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("개행문자확인");
		System.out.print("\\n개행문자확인\n");
		System.out.print("\\r개행문자확인\r");
		System.out.println("개행자동메서드");
		System.out.println("apple\torange");
		System.out.print("morning\ra");
		System.out.println("\"따옴표 출력\"");		
	}

}
