package Ch01;

public class FormC {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.printf("%d + %d = %d\n", 5, 12, 5+12);
		System.out.printf("%d + %d = %d\n", 7, 3, 7+12);
		
		System.out.printf("실수형 상수 : %f\n", 123.456);
		System.out.printf("정수형 상수 : %d\n", (int)123.456);
		System.out.printf("실수형 상수 : %.15f\n", 123.45678912345678);
		
		System.out.printf("문자 A의 표현 : %c\n", 'A');
		System.out.printf("문자열의 표현 : %s\n", "This is a String");
	}

}
