package Ch04;

public class ShiftArith {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 15;
		int num2 = 20;
		int num3 = num1 << 2;
		int num4 = num2 >> 2;
		System.out.printf("<< Shift 연산 결과 : %d\n", num3);
		System.out.printf(">> Shift 연산 결과 : %d\n", num4);
	}

}
